(2, 2, 'get_locations', 'public', 'ajax function to display custom location select field', '', '(via POST) location_id', '[{"attr_name":"return:","attr_desc":"\\t custom select field for location\\n\\t"}]', 'function get_locations()\n	{\n		$locations = $this->location_model->get_locations($this->input->post(''location_id''));\n		$location_id = $this->input->post(''location_id'');\n		$child_selected = $this->input->post(''child_selected'');\n		if ($this->input->post(''location_id'') && count($locations) > 0)\n		{\n			$array = array();\n			foreach($locations as $location)\n			{\n				$array[] = array(\n					''value'' => $location[''location_id''],\n					''label'' => $location[''name'']\n				);\n			}\n			echo modules::run(''common/field_select'', $array, ''location_id'', $child_selected);\n		}\n		\n	}\n	\n	\n	// begin roles', '2014-02-28 05:18:30', '0000-00-00 00:00:00'),
(3, 2, 'get_roles', 'public', 'Displayes all the available roles in the system. The user can then sort the data based on Name or Frequency.', '', 'Post data - gets sort paramater', '[{"attr_name":"return","attr_desc":"\\t Lists all roles\\n\\t"}]', 'function get_roles()\n	{\n		$params = $this->input->post(''params'',true);\n		$data[''roles''] = $this->role_model->get_roles($params);\n		$data[''params''] = $params;\n		$this->load->view(''roles/ajax_list_roles'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:30', '0000-00-00 00:00:00'),
(4, 2, 'add_role', 'public', 'Adds new role', '', 'Post data - name of the new role', '[{"attr_name":"return","attr_desc":"\\t \\n\\t"}]', 'function add_role()\n	{\n		$data[''name''] = $this->input->post(''name'',true);\n		$this->role_model->insert_role($data);\n		echo ''success'';\n	}', '2014-02-28 05:18:30', '0000-00-00 00:00:00'),
(5, 2, 'edit_role', 'public', 'Edit role', '', 'Post data - updated name of the role', '[{"attr_name":"return","attr_desc":"\\t \\n\\t"}]', 'function edit_role()\n	{\n		$data[''name''] = $this->input->post(''new_name'',true);\n		$edit_id = $this->input->post(''role_id'',true);\n		$this->role_model->update_role($edit_id, $data);\n		echo ''success'';\n	}', '2014-02-28 05:18:30', '0000-00-00 00:00:00'),
(6, 2, 'delete_role', 'public', 'Delete roles', 'Removes roles assigned to staff as well.', 'null', '[{"attr_name":"return","attr_desc":"\\t \\n\\t"},{"attr_name":"action_required","attr_desc":"\\t Create staff and role relationship table\\n\\t"}]', 'function delete_role()\n	{\n		$role_id = $this->input->post(''role_id'',true);\n		$this->role_model->delete_role($role_id);\n		echo ''success'';\n	}\n	\n	// end roles \n	\n	// begin venues', '2014-02-28 05:18:30', '0000-00-00 00:00:00'),
(7, 2, 'get_roles', 'public', 'Displayes all the available venues in the system. The user can then sort the data based on Name or Frequency.', '', 'Post data - gets sort paramater', '[{"attr_name":"return","attr_desc":"\\t Lists all roles\\n\\t"}]', 'function get_roles()\n	{\n		$params = $this->input->post(''params'',true);\n		$data[''roles''] = $this->role_model->get_roles($params);\n		$data[''params''] = $params;\n		$this->load->view(''roles/ajax_list_roles'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:30', '0000-00-00 00:00:00'),
