(30, 16, 'get_roles', 'public', 'This function queries the database and returns the list of roles based on the sort parameter. The roles are sorted in the ascending order of their name by default.', '', 'string(sort parameter)', '[{"attr_name":"return","attr_desc":"\\t Returns array of avaliable roles\\n\\t"}]', 'function get_roles($params = '''')\n	{\n		$sql = "select attribute_roles.*, (select count(staffs_roles_id) from staffs_roles where attribute_roles.role_id = staffs_roles.attribute_role_id) as frequency from attribute_roles";\n		if($params){\n			$sort_param = json_decode($params);	\n			$sql .= " order by $sort_param->sort_by $sort_param->sort_order";\n		}else{\n			$sql .= " order by name asc";\n		}\n		$query = $this->db->query($sql);\n		return $query->result_array();\n	}', '2014-02-28 05:18:30', '0000-00-00 00:00:00'),
