(42, 26, 'field_select_countries', 'public', 'custom select countries field', '', '- $field_name: string of field name', '[{"attr_name":"-","attr_desc":"\\t\\t\\t $field_value (optional): selected country code\\n\\t"},{"attr_name":"-","attr_desc":"\\t\\t\\t $size (optional): size \\n\\t"},{"attr_name":"return:","attr_desc":"\\t custom view of select states field\\n\\t"}]', 'function field_select_countries($field_name, $field_value=null, $size=null) {\n		$countries = $this->common_model->get_countries();\n		if ($field_value == null || $field_value=='''')\n		{\n			$field_value = ''AU'';\n		}\n		$array = array();\n		foreach($countries as $country)\n		{\n			$array[] = array(\n				''value'' => $country[''code''],\n				''label'' => $country[''name'']\n			);\n		}\n		return $this->field_select($array, $field_name, $field_value, $size);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
