(60, 38, '_insert_data', 'private', 'This function is used to parse the data posted form formbuilder interface and prepare the data for insertion. Once the data has been prepared it is then inserted into the database.', '', 'object(form_elements),string(type of element),string(state if the form element is inline, default = no)', '[{"attr_name":"return","attr_desc":"\\t does not return anything, simply adds the data to the database\\n\\t"}]', 'function _insert_data($decoded_obj,$type,$decoded_sort_order,$inline_multi = ''no'')\n	{\n		$decoded_array = (array)$decoded_obj;\n		//echo ''<pre>''.print_r($decoded_array,true).''</pre>'';exit(0);\n		\n		if($decoded_array && $type){\n			switch($type){\n				case ''textinput'':\n					foreach($decoded_array as $arr){\n						$data = array(\n							''type'' => $type,\n							''label'' => $arr->label,\n							''name'' => $arr->name,\n							''placeholder'' => $arr->placeholder,\n							''order'' => $this->_get_element_order($decoded_sort_order,$arr->name)\n							);	\n						$this->formbuilder_model->insert_form($data);\n					}\n				break;	\n				\n				case ''textarea'':\n					foreach($decoded_array as $arr){\n						$data = array(\n							''type'' => $type,\n							''label'' => $arr->label,\n							''name'' => $arr->name,\n							''order'' => $this->_get_element_order($decoded_sort_order,$arr->name)\n							);	\n						$this->formbuilder_model->insert_form($data);\n					}\n				break;\n				\n				case ''radio'':\n					foreach($decoded_array as $arr){\n						$data = array(\n							''type'' => $type,\n							''label'' => $arr[''attr'']->label,\n							''name'' => $arr[''attr'']->name,\n							''inline_element'' => $inline_multi,\n							''attributes'' => json_encode($arr[''values'']),\n							''order'' => $this->_get_element_order($decoded_sort_order,$arr[''attr'']->name)\n							);	\n						$this->formbuilder_model->insert_form($data);\n					}\n				break;\n				\n				case ''checkbox'':\n					foreach($decoded_array as $arr){\n						$data = array(\n							''type'' => $type,\n							''label'' => $arr[''attr'']->label,\n							''name'' => $arr[''attr'']->name,\n							''multi_select'' => ''yes'',\n							''inline_element'' => $inline_multi,\n							''attributes'' => json_encode($arr[''values'']),\n							''order'' => $this->_get_element_order($decoded_sort_order,$arr[''attr'']->name)\n							);	\n						$this->formbuilder_model->insert_form($data);\n					}\n				break;\n				\n				case ''select'':\n					foreach($decoded_array as $arr){\n						$data = array(\n							''type'' => $type,\n							''label'' => $arr[''attr'']->label,\n							''name'' => $arr[''attr'']->name,\n							''multi_select'' => $inline_multi,\n							''attributes'' => json_encode($arr[''values'']),\n							''order'' => $this->_get_element_order($decoded_sort_order,$arr[''attr'']->name)\n							);	\n						$this->formbuilder_model->insert_form($data);\n					}\n				break;\n				\n				case ''filebutton'':\n					foreach($decoded_array as $arr){\n						$data = array(\n							''type'' => $type,\n							''label'' => $arr->label,\n							''name'' => $arr->name,\n							''order'' => $this->_get_element_order($decoded_sort_order,$arr->name)\n							);	\n						$this->formbuilder_model->insert_form($data);\n					}\n				break;\n			}\n		}\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(61, 38, '_get_element_order', 'private', 'This returns the order of the element', '', 'array(array of elements and their order),string(element name)', '[{"attr_name":"return","attr_desc":"\\t returns json decoded array that contains objects in inner levels\\n\\t"}]', 'function _get_element_order($order_array,$element_name)\n	{\n		if($order_array && $element_name){\n			foreach($order_array as $key => $val){\n					if($key == $element_name){\n						return $val;	\n					}\n			}\n		}\n		return 0;	\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(62, 38, '_manual_jason_decode', 'private', 'This function is used to manually decode the json data. This was done as the php build in json_decode failed to decode the multi level json string.', '', 'json(form elements),enum(status if the data has multi dimension)', '[{"attr_name":"return","attr_desc":"\\t returns json decoded array that contains objects in inner levels\\n\\t"}]', 'function _manual_jason_decode($data,$multi = false)\n	{\n		$temp_arr = array();\n		$final_arr = array();\n		$count = 0;\n		$separator = $multi ? ''],'' : ''},'';\n		$post_fix = $multi ? '']'' : ''}'';\n		if($data){\n			if(!$multi){\n				$temp_arr = explode($separator,$data);\n				if(count($temp_arr) > 1){	\n					$temp_arr[0] = $temp_arr[0].$post_fix;\n				}\n				foreach($temp_arr as $arr){\n					$final_arr[$count] = json_decode($arr);	\n					$count++;	\n				}\n			}else{\n				$temp_arr = explode($separator,$data);\n				if(count($temp_arr) > 1){		\n					$temp_arr[0] = $temp_arr[0].$post_fix;	\n				}\n				$multi_arr = array();\n				foreach($temp_arr as $arr){\n					$multi_arr = json_decode($arr);\n					$inner_counter = 0;\n					$temp_multi_arr = array();\n					if($multi_arr){\n						foreach($multi_arr as $ma){\n							if($inner_counter == 0){\n								$temp_multi_arr[''attr''] = json_decode($ma); 	\n							}else{\n								$temp_multi_arr[''values''][$inner_counter] = json_decode($ma); 	\n							}\n							$inner_counter++;\n						}\n					}\n					$final_arr[$count] = $temp_multi_arr;\n					$count++;	\n				}\n			}\n		}\n		return $final_arr;\n		\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(63, 38, '_manual_jason_decode', 'private', 'This function is used to manually decode the json data for sort order. This was done as the php build in json_decode failed to decode the multi level json string.', '', 'json(form elements)', '[{"attr_name":"return","attr_desc":"\\t returns json decoded array \\n\\t"}]', 'function _manual_jason_decode($data,$multi = false)\n	{\n		$temp_arr = array();\n		$final_arr = array();\n		$count = 0;\n		$separator = $multi ? ''],'' : ''},'';\n		$post_fix = $multi ? '']'' : ''}'';\n		if($data){\n			if(!$multi){\n				$temp_arr = explode($separator,$data);\n				if(count($temp_arr) > 1){	\n					$temp_arr[0] = $temp_arr[0].$post_fix;\n				}\n				foreach($temp_arr as $arr){\n					$final_arr[$count] = json_decode($arr);	\n					$count++;	\n				}\n			}else{\n				$temp_arr = explode($separator,$data);\n				if(count($temp_arr) > 1){		\n					$temp_arr[0] = $temp_arr[0].$post_fix;	\n				}\n				$multi_arr = array();\n				foreach($temp_arr as $arr){\n					$multi_arr = json_decode($arr);\n					$inner_counter = 0;\n					$temp_multi_arr = array();\n					if($multi_arr){\n						foreach($multi_arr as $ma){\n							if($inner_counter == 0){\n								$temp_multi_arr[''attr''] = json_decode($ma); 	\n							}else{\n								$temp_multi_arr[''values''][$inner_counter] = json_decode($ma); 	\n							}\n							$inner_counter++;\n						}\n					}\n					$final_arr[$count] = $temp_multi_arr;\n					$count++;	\n				}\n			}\n		}\n		return $final_arr;\n		\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
