(78, 40, 'list_items', 'public', 'ajax function to list all items of the invoice', '', '(POST) invoice_id', '[{"attr_name":"return:","attr_desc":"\\t (html) view of the items list\\n\\t"}]', 'function list_items() {\n		$invoice_id = $this->input->post(''invoice_id'');\n		$invoice = $this->invoice_model->get_invoice($invoice_id);\n		$data[''invoice''] = $invoice;\n		$data[''items''] = $this->invoice_model->get_invoice_items($invoice_id);\n		$this->load->view(''create/items_list'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(79, 40, 'get_total', 'public', 'ajax function to get the total charge details', '', '(POST) invoice_id', '[{"attr_name":"return:","attr_desc":"\\t (html) view of the total charge details\\n\\t"}]', 'function get_total() {\n		$invoice_id = $this->input->post(''invoice_id'');\n		$total = 0;\n		$gst = 0;\n		$items = $this->invoice_model->get_invoice_items($invoice_id);\n		foreach($items as $item) {\n			$total += $item[''amount''];\n			if ($item[''tax'']) {\n				$gst += $item[''amount''] / 11;\n			}\n		}\n		$this->invoice_model->update_invoice($invoice_id, array(''gst'' => $gst,''total_amount'' => $total));\n		$data[''invoice''] = $this->invoice_model->get_invoice($invoice_id);\n		$data[''total''] = $total;\n		$data[''gst''] = $gst;\n		$this->load->view(''create/charge_details'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(80, 40, 'add_item', 'public', 'add new item to the invoice', '', '(POST) array of data', '[{"attr_name":"return:","attr_desc":"\\t (void)\\n\\t"}]', 'function add_item() {\n		$data = $this->input->post();\n		if ($data[''tax''] == GST_ADD) {\n			$data[''amount''] = $data[''amount''] * 1.1;\n		}\n		$this->invoice_model->add_invoice_item($data);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(81, 40, 'check_breakdown', 'public', 'ajax function to check if the invoice can be break down or not', '', '(POST) invoice_id', '[{"attr_name":"return:","attr_desc":"\\t (string) \\"true\\" or \\"false\\"\\n\\t"}]', 'function check_breakdown() {\n		$invoice_id = $this->input->post(''invoice_id'');\n		$invoice = $this->invoice_model->get_invoice($invoice_id);\n		$jobs = unserialize($invoice[''jobs'']);\n		$items = $this->invoice_model->get_invoice_items($invoice_id);\n		foreach($items as $key => $item) {\n			if (!$item[''include_timesheets'']) {\n				unset($items[$key]);\n			}\n		}\n		if (count($items) == count($jobs)) {\n			echo ''true'';\n		} else {\n			echo ''false'';\n		}\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(82, 40, 'show_breakdown', 'public', 'ajax function to enable/disable break down items of the invoice', '', '(POST) invoice_id', '[{"attr_name":"return:","attr_desc":"\\t (html) view of the break down item list\\n\\t"}]', 'function show_breakdown() {\n		$invoice_id = $this->input->post(''invoice_id'');\n		if ($this->input->post(''show'') == "true") {\n			$data[''items''] = $this->invoice_model->get_invoice_items($invoice_id);\n			$this->invoice_model->update_invoice($invoice_id, array(''breakdown'' => 1));\n			$this->load->view(''create/breakdown'', isset($data) ? $data : NULL);\n		} else {			\n			$this->invoice_model->update_invoice($invoice_id, array(''breakdown'' => 0));\n		}\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
