(86, 40, 'mark_as_paid', 'public', 'ajax function to mark an invoice as paid', '', '(POST) invoice_id', '[{"attr_name":"return:","attr_desc":"\\t (html) menu dropdown status of the invoice\\n\\t"}]', 'function mark_as_paid() {\n		$invoice_id = $this->input->post(''invoice_id'');\n		# Update invoice\n		$this->invoice_model->update_invoice($invoice_id, array(\n			''status'' => INVOICE_PAID,\n			''paid_on'' => date(''Y-m-d H:i:s'')\n		));\n		# Update timesheet\n		$this->invoice_model->mark_paid_timesheets($invoice_id);\n		echo modules::run(''invoice/menu_dropdown_status'', $invoice_id);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
