(93, 41, 'edit', 'public', 'edit invoice page', '', '(int) $invoice_id', '[{"attr_name":"return:","attr_desc":"\\t (html) edit invoice page\\n\\t"}]', 'function edit($invoice_id) {\n		$invoice = $this->invoice_model->get_invoice($invoice_id);\n		# If invoice is generated, cannot edit anymore, go to view page\n		if ($invoice[''status''] == INVOICE_GENERATED) {\n			redirect(''invoice/view/'' . $invoice_id);\n		}\n		$data[''invoice''] = $invoice;\n		$data[''client''] = modules::run(''client/get_client'', $invoice[''client_id'']);\n		if ($invoice[''breakdown'']) {\n			$data[''items''] = $this->invoice_model->get_invoice_items($invoice_id);\n		}\n		$this->load->view(''create/edit_view'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(94, 41, 'generate', 'public', 'generate the invoice and link timesheets to the invoice', '', '(int) $invoice_id', '[{"attr_name":"return:","attr_desc":"\\t (void) redirect to view invoice page\\n\\t"}]', 'function generate($invoice_id) {\n		$invoice = $this->invoice_model->get_invoice($invoice_id);\n		# Update invoice status\n		$user = $this->session->userdata(''user_data'');\n		$this->invoice_model->update_invoice($invoice_id, array(\n			''status'' => INVOICE_GENERATED,\n			''issued_by'' => $user[''user_id'']\n		));\n		$this->invoice_model->generate_invoice_timesheets($invoice[''client_id''], $invoice_id);\n		redirect(''invoice/view/'' . $invoice_id);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(95, 41, 'download', 'public', 'generate and download the pdf', '', '(int) $invoice_id', '[{"attr_name":"return:","attr_desc":"\\t (void)\\n\\t"}]', 'function download($invoice_id) {\n		# As PDF creation takes a bit of memory, we''re saving the created file in /uploads/pdf/\n		$filename = "invoice_" . $invoice_id;\n		$pdfFilePath = "./uploads/pdf/$filename.pdf";\n		\n		 \n		ini_set(''memory_limit'',''32M''); # boost the memory limit if it''s low \n		\n		$invoice = $this->invoice_model->get_invoice($invoice_id);\n		$data[''invoice''] = $invoice;\n		$data[''client''] = modules::run(''client/get_client'', $invoice[''client_id'']);\n		$data[''items''] = $this->invoice_model->get_invoice_items($invoice_id);\n		$html = $this->load->view(''create/download_view'', isset($data) ? $data : NULL, true); \n		\n				\n		$this->load->library(''pdf'');\n		$pdf = $this->pdf->load(); 			\n		$stylesheet = file_get_contents(''./assets/css/pdf.css'');\n		$pdf->WriteHTML($stylesheet,1);\n		$pdf->WriteHTML($html,2);\n		$pdf->Output($pdfFilePath, ''F''); // save to file \n		 \n		redirect("./uploads/pdf/$filename.pdf"); \n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(96, 41, 'view', 'public', 'view of the single invoice', '', '(int) $invoice_id', '[{"attr_name":"return:","attr_desc":"\\t (html) view invoice page \\n\\t"}]', 'function view($invoice_id) {\n		$invoice = $this->invoice_model->get_invoice($invoice_id);\n		$data[''invoice''] = $invoice;\n		$data[''client''] = modules::run(''client/get_client'', $invoice[''client_id'']);\n		$data[''items''] = $this->invoice_model->get_invoice_items($invoice_id);\n		$this->load->view(''create/generated_view'', isset($data) ? $data : NULL); \n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(97, 41, 'row_job', 'public', 'view of row of a job', '', '(int) $job_id', '[{"attr_name":"return:","attr_desc":"\\t (html) row (tr) of the job\\n\\t"}]', 'function row_job($job_id) {\n		$data[''job''] = $this->invoice_model->get_job($job_id);\n		$data[''timesheets''] = $this->invoice_model->get_timesheets($job_id);\n		$this->load->view(''source/job_row_view'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(98, 41, 'row_timesheet', 'public', 'view of row of a timesheet', '', '(int) $timesheet_id', '[{"attr_name":"return:","attr_desc":"\\t (html) row (tr) of a job\\n\\t"}]', 'function row_timesheet($timesheet_id) {\n		$data[''timesheet''] = $this->invoice_model->get_timesheet($timesheet_id);\n		$this->load->view(''source/timesheet_row_view'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00');
INSERT INTO `modules_functions` (`id`, `modules_mvc_id`, `name`, `access`, `description`, `comment`, `params`, `attributes`, `code`, `created`, `modified`) VALUES
(99, 41, 'get_job_timesheets', 'public', 'get invoiced timesheets of an invoiced job', '', '(int) $job_id', '[{"attr_name":"(int)","attr_desc":"\\t\\t\\t $status\\n\\t"},{"attr_name":"return:","attr_desc":"\\t (array) of time sheet objects\\n\\t"}]', 'function get_job_timesheets($job_id, $status) {\n		return $this->invoice_model->get_job_timesheets($job_id, $status);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(100, 41, 'field_select_status', 'public', 'custom field select status of invoice', '', '- $field_name: string of field name', '[{"attr_name":"-","attr_desc":"\\t\\t\\t $field_value (optional): selected value of field\\n\\t"},{"attr_name":"-","attr_desc":"\\t\\t\\t $size (optional): size \\n\\t"},{"attr_name":"return:","attr_desc":"\\t custom select input field\\n\\t"}]', 'function field_select_status($field_name, $field_value=null, $size=null) {\n		$array = array(\n			array(''value'' => INVOICE_GENERATED, ''label'' => ''Unpaid''),\n			array(''value'' => INVOICE_PAID, ''label'' => ''Paid'')\n		);\n		return modules::run(''common/field_select'', $array, $field_name, $field_value, $size);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(101, 41, 'menu_dropdown_status', 'public', 'generate the dropdown menu of invoice status', '', '(int) $invoice_id', '[{"attr_name":"return:","attr_desc":"\\t (html) dropdown menu of invoice status\\n\\t"}]', 'function menu_dropdown_status($invoice_id) {\n		$data[''invoice''] = $this->invoice_model->get_invoice($invoice_id);\n		$this->load->view(''search/menu_dropdown_status'', isset($data) ? $data : NULL);\n	}\n	\n}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(102, 42, 'add_client_invoice', 'public', 'create the client invoice', '', '(array) $data', '[{"attr_name":"return:","attr_desc":"\\t (int) invoice_id\\n\\t"}]', 'function add_client_invoice($data) {\n		$this->db->insert(''invoices'', $data);\n		return $this->db->insert_id();\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(103, 42, 'add_invoice_item', 'public', 'add item to the invoice', '', '(array) $data', '[{"attr_name":"return:","attr_desc":"\\t (boolean)\\n\\t"}]', 'function add_invoice_item($data) {\n		$this->db->insert(''invoice_items'', $data);\n		return $this->db->insert_id();\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(104, 42, 'get_job_timesheets', 'public', 'get invoiced time sheet of invoiced job', '', '(int) $job_id', '[{"attr_name":"(int)","attr_desc":"\\t\\t\\t $invoice_status\\n\\t"},{"attr_name":"return:","attr_desc":"\\t (array) of time sheet objects\\n\\t"}]', 'function get_job_timesheets($job_id, $invoice_status) {\n		$this->db->where(''job_id'', $job_id);\n		$this->db->where(''status'', TIMESHEET_BATCHED);\n		$this->db->where(''status_invoice_client >='', $invoice_status);\n		$query = $this->db->get(''job_shift_timesheets'');\n		return $query->result_array();\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(105, 42, 'delete_invoice_item', 'public', 'delete an item from the invoice', '', '(int) $item_id', '[{"attr_name":"return:","attr_desc":"\\t (boolean)\\n\\t"}]', 'function delete_invoice_item($item_id) {\n		$this->db->where(''item_id'', $item_id);\n		return $this->db->delete(''invoice_items'');\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(106, 42, 'delete_invoice_items', 'public', 'delete all items of an invoice', '', '(int) $invoice_id', '[{"attr_name":"return:","attr_desc":"\\t (boolean)\\n\\t"}]', 'function delete_invoice_items($invoice_id) {\n		$this->db->where(''invoice_id'', $invoice_id);\n		return $this->db->delete(''invoice_items'');\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(107, 42, 'delete_invoice', 'public', 'delete the invoice itselft', '', '(int) $invoice_id', '[{"attr_name":"return:","attr_desc":"\\t (boolean)\\n\\t"}]', 'function delete_invoice_item($item_id) {\n		$this->db->where(''item_id'', $item_id);\n		return $this->db->delete(''invoice_items'');\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(108, 42, 'get_invoice_items', 'public', 'get all items of an invoice', '', '(int) $invoice_id', '[{"attr_name":"return:","attr_desc":"\\t (array) of item objects\\n\\t"}]', 'function get_invoice_items($invoice_id) {\n		$this->db->where(''invoice_id'', $invoice_id);\n		$this->db->order_by(''job_id'', ''asc'');\n		$query = $this->db->get(''invoice_items'');\n		return $query->result_array();\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(109, 42, 'update_invoice', 'public', 'update invoice object', '', '(int) $invoice_id', '[{"attr_name":"(array)","attr_desc":"\\t\\t\\t $data\\n\\t"},{"attr_name":"return:","attr_desc":"\\t (boolean)\\n\\t"}]', 'function update_invoice($invoice_id, $data) {\n		$this->db->where(''invoice_id'', $invoice_id);\n		return $this->db->update(''invoices'', $data);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(110, 42, 'get_invoice', 'public', 'get invoice object by id', '', '(int) $invoice_id', '[{"attr_name":"return:","attr_desc":"\\t (object) invoice\\n\\t"}]', 'function get_invoice_items($invoice_id) {\n		$this->db->where(''invoice_id'', $invoice_id);\n		$this->db->order_by(''job_id'', ''asc'');\n		$query = $this->db->get(''invoice_items'');\n		return $query->result_array();\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(111, 42, 'get_client_invoice', 'public', 'get ready timesheet to be invoiced of a client', '', '(int) $user_id', '[{"attr_name":"return:","attr_desc":"\\t array of time sheet objects\\n\\t"}]', 'function get_client_invoice($user_id) {\n		$sql = "SELECT j.*, sum(js.total_amount_client) as `total_amount` FROM `job_shift_timesheets` js\n						LEFT JOIN `jobs` j ON js.job_id = j.job_id\n						WHERE js.status = " . TIMESHEET_BATCHED . "\n						AND js.status_invoice_client = " . INVOICE_READY . "\n						AND js.client_id = " . $user_id . "\n						GROUP BY js.job_id";\n		$query = $this->db->query($sql);\n		return $query->result_array();\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(112, 42, 'get_invoiced_clients', 'public', 'get clients that has temporary invoices', '', '', '[{"attr_name":"return:","attr_desc":"\\t array of client objects\\n\\t"}]', 'function get_invoiced_clients() {\n		$sql = "SELECT uc.*, sum(j.total_amount_client) as `total_amount`, count(*) as `total_timesheets` FROM `job_shift_timesheets` j\n					LEFT JOIN `user_clients` uc ON j.client_id = uc.user_id\n					WHERE j.status = " . TIMESHEET_BATCHED . "\n					AND j.status_invoice_client = " . INVOICE_READY . "\n					GROUP BY j.client_id";\n		$query = $this->db->query($sql);\n		return $query->result_array();\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(113, 42, 'get_clients', 'public', 'get list of clients that has timesheets can be billed', '', '', '[{"attr_name":"return:","attr_desc":"\\t (array) of client objects\\n\\t"}]', 'function get_clients() {\n		$sql_select_job_id = "SELECT job_id \n								FROM `job_shift_timesheets` \n								WHERE status = " . TIMESHEET_BATCHED . "\n								AND status_invoice_client <= " . INVOICE_READY . "\n								GROUP BY job_id";\n		$sql = "SELECT uc.*, count(*) as `total_jobs` FROM user_clients uc\n					LEFT JOIN jobs j ON j.client_id = uc.user_id\n					WHERE j.job_id IN ($sql_select_job_id) GROUP BY uc.user_id";\n		$query = $this->db->query($sql);\n		return $query->result_array();\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(114, 42, 'get_client_jobs', 'public', 'get list of jobs that has timesheets can be billed', '', '', '[{"attr_name":"return:","attr_desc":"\\t (array) of job objects\\n\\t"}]', 'function get_client_jobs($user_id) {\n		$sql_select_job_id = "SELECT job_id \n								FROM `job_shift_timesheets` \n								WHERE status = " . TIMESHEET_BATCHED . "\n								AND status_invoice_client <= " . INVOICE_READY . "\n								GROUP BY job_id";\n		$sql = "SELECT * FROM jobs WHERE client_id = " . $user_id . " AND job_id IN ($sql_select_job_id)";\n		$query = $this->db->query($sql);\n		return $query->result_array();\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(115, 42, 'get_job', 'public', 'get a job object by id', '', '(int) $job_id', '[{"attr_name":"return:","attr_desc":"\\t (object) of job\\n\\t"}]', 'function get_job_timesheets($job_id, $invoice_status) {\n		$this->db->where(''job_id'', $job_id);\n		$this->db->where(''status'', TIMESHEET_BATCHED);\n		$this->db->where(''status_invoice_client >='', $invoice_status);\n		$query = $this->db->get(''job_shift_timesheets'');\n		return $query->result_array();\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(116, 42, 'get_timesheet', 'public', 'get a time sheet by id', '', '(int) $timesheet_id', '[{"attr_name":"return:","attr_desc":"\\t (object) of time sheet\\n\\t"}]', 'function get_timesheet($timesheet_id) {\n		$this->db->where(''timesheet_id'', $timesheet_id);\n		$this->db->where(''status'', TIMESHEET_BATCHED);\n		$query = $this->db->get(''job_shift_timesheets'');\n		return $query->first_row(''array'');\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(117, 42, 'get_timesheets', 'public', 'get list of ready time sheets oj a job', '', '(int) $job_id', '[{"attr_name":"return:","attr_desc":"\\t (array) of time sheet objects\\n\\t"}]', 'function get_timesheets($job_id) {\n		$this->db->where(''job_id'', $job_id);\n		$this->db->where(''status'', TIMESHEET_BATCHED);\n		$this->db->where(''status_invoice_client <='', INVOICE_READY);\n		$query = $this->db->get(''job_shift_timesheets'');\n		return $query->result_array();\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(118, 42, 'add_job_to_invoice', 'public', 'add all time sheets of the job to the invoice', '', '(int) $job_id', '[{"attr_name":"return:","attr_desc":"\\t (boolean)\\n\\t"}]', 'function add_job_to_invoice($job_id) {\n		$this->db->where(''job_id'', $job_id);\n		return $this->db->update(''job_shift_timesheets'', array(''status_invoice_client'' => INVOICE_READY));\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(119, 42, 'remove_job_from_invoice', 'public', 'remove all time sheets of the job from the invoice', '', '(int) $job_id', '[{"attr_name":"return:","attr_desc":"\\t (boolean)\\n\\t"}]', 'function remove_job_from_invoice($job_id) {\n		$this->db->where(''job_id'', $job_id);\n		return $this->db->update(''job_shift_timesheets'', array(''status_invoice_client'' => INVOICE_PENDING));\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(120, 42, 'add_timesheet_to_invoice', 'public', 'update the timesheet invoice status to ready', '', '(int) $timesheet_id', '[{"attr_name":"return:","attr_desc":"\\t (boolean)\\n\\t"}]', 'function add_timesheet_to_invoice($timesheet_id) {\n		$this->db->where(''timesheet_id'', $timesheet_id);\n		return $this->db->update(''job_shift_timesheets'', array(''status_invoice_client'' => INVOICE_READY));		\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(121, 42, 'remove_timesheet_from_invoice', 'public', 'update the timesheet invoice status to pending', '', '(int) $timesheet_id', '[{"attr_name":"return:","attr_desc":"\\t (boolean)\\n\\t"}]', 'function remove_timesheet_from_invoice($timesheet_id) {\n		$this->db->where(''timesheet_id'', $timesheet_id);\n		return $this->db->update(''job_shift_timesheets'', array(''status_invoice_client'' => INVOICE_PENDING));\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(122, 42, 'generate_invoice_timesheets', 'public', 'linked all timesheets to the invoice and update time sheets status to invoice generated', '', '(int) $client_id', '[{"attr_name":"(int)","attr_desc":"\\t\\t\\t $invoice_id\\n\\t"},{"attr_name":"return:","attr_desc":"\\t (boolean)\\n\\t"}]', 'function generate_invoice_timesheets($client_id, $invoice_id) {\n		$data = array(\n			''invoice_id'' => $invoice_id,\n			''status_invoice_client'' => INVOICE_GENERATED\n		);\n		$this->db->where(''client_id'', $client_id);\n		$this->db->where(''status_invoice_client'', INVOICE_READY);\n		$this->db->where(''invoice_id'', 0);		\n		return $this->db->update(''job_shift_timesheets'', $data);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(123, 42, 'mark_paid_timesheets', 'public', 'update timesheets of an invoice to paid status', '', '(int) $invoice_id', '[{"attr_name":"return:","attr_desc":"\\t (boolean)\\n\\t"}]', 'function mark_paid_timesheets($invoice_id) {\n		$data = array(\n			''status_invoice_client'' => INVOICE_PAID,\n			''client_paid_on'' => date(''Y-m-d H:i:s'')\n		);\n		$this->db->where(''invoice_id'', $invoice_id);\n		return $this->db->update(''job_shift_timesheets'', $data);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(124, 42, 'mark_unpaid_timesheets', 'public', 'update timesheets of an invoice to unpaid status', '', '(int) $invoice_id', '[{"attr_name":"return:","attr_desc":"\\t (boolean)\\n\\t"}]', 'function mark_unpaid_timesheets($invoice_id) {\n		$data = array(\n			''status_invoice_client'' => INVOICE_GENERATED\n		);\n		$this->db->where(''invoice_id'', $invoice_id);\n		return $this->db->update(''job_shift_timesheets'', $data);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(125, 42, 'search_invoices', 'public', 'search invoices by parameters', '', '(array)', '[{"attr_name":"return:","attr_desc":"\\t (array) of invoice objects\\n\\t"}]', 'function search_invoices($params) {\n		if (isset($params[''client_id'']) && $params[''client_id''] != 0 ) {\n			$this->db->where(''client_id'', $params[''client_id'']);\n		}\n		if (isset($params[''issued_by'']) && $params[''issued_by''] != 0) {\n			$this->db->where(''issued_by'', $params[''issued_by'']);\n		}\n		if (isset($params[''status'']) && $params[''status''] != 0) {\n			$this->db->where(''status'', $params[''status'']);\n		}\n		if (isset($params[''keywords'']) && $params[''keywords''] != '''') {\n			$this->db->like(''jobs'', $params[''keywords'']);\n		}\n		if (isset($params[''date_from'']) && $params[''date_from''] != '''') {\n			$date_from = date(''Y-m-d'', strtotime($params[''date_from'']));\n			$this->db->where(''issued_date >='', $date_from);\n		}\n		if (isset($params[''date_to'']) && $params[''date_to''] != '''') {\n			$date_to = date(''Y-m-d'', strtotime($params[''date_to'']));\n			$this->db->where(''issued_date <='', $date_to);\n		}\n		$this->db->where(''status > '', 0);\n		$query = $this->db->get(''invoices'');\n		return $query->result_array();\n	}\n}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(126, 43, '', '', 'ajax controller, provides ajax functions for Job module', '', '', '[{"attr_name":"author:","attr_desc":"\\t namnd86@gmail.com\\n "}]', '', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(127, 43, 'search_jobs', 'public', 'ajax function to search job(s)', '', 'an array of search parameters (POST)', '[{"attr_name":"return:","attr_desc":"\\t view of list of jobs\\n\\t"}]', 'function search_jobs()\n	{\n		$data = $this->input->post();\n		$data[''jobs''] = $this->job_model->search_jobs($data);\n		$this->load->view(''jobs_search_results_view'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(128, 43, 'create_shifts', 'public', 'ajax function to create shift(s) for a job', '', 'an array of shift parameters (POST)', '[{"attr_name":"return:","attr_desc":"\\t json encode of result\\n\\t"},{"attr_name":"-","attr_desc":"\\t\\t\\t failed: {ok: false, error_id: (string)}\\n\\t"},{"attr_name":"-","attr_desc":"\\t\\t\\t success: {ok: true, job_date: (string) YYYYDDMM\\n\\t"}]', 'function create_shifts()\n	{\n		$data = $this->input->post();\n		$filter_data = array();\n		$filter_data[''job_id''] = $data[''job_id''];\n		$filter_data[''job_date''] = date(''Y-m-d'', strtotime($data[''job_date'']));\n		\n		if (strtotime($data[''job_date'']) <= now())\n		{\n			# Job start date can not be in the past\n			echo json_encode(array(''ok'' => false, ''error_id'' => ''start_date''));\n			return;\n		}	\n		\n		$filter_data[''start_time''] = strtotime($data[''job_date'']);		\n		$filter_data[''finish_time''] = strtotime($data[''finish_time'']);\n				\n		\n		if ($filter_data[''finish_time''] <= $filter_data[''start_time''])\n		{\n			# Finish time can not be less than start time\n			echo json_encode(array(''ok'' => false, ''error_id'' => ''finish_time''));\n			return;\n		}\n		\n		if ($data[''break_length''] > 0)\n		{\n			$break_time = array(\n				''length'' => $data[''break_length''] * 60, # seconds\n				''start_at'' => strtotime($data[''break_start_at''])\n			);\n			\n			if ($break_time[''start_at''] <= $filter_data[''start_time''] || $break_time[''start_at''] >= $filter_data[''finish_time''])\n			{\n				echo json_encode(array(''ok'' => false, ''error_id'' => ''break_start_at''));\n				return;\n			}\n			$breaks = array();\n			$breaks[] = $break_time;\n			$filter_data[''break_time''] = json_encode($breaks);\n		}\n		if ($data[''venue''])\n		{\n			$venue = modules::run(''attribute/venue/get_venue_by_name'', $data[''venue'']);\n			if (!$venue)\n			{\n				echo json_encode(array(''ok'' => false, ''error_id'' => ''venue''));\n				return;\n			}\n			else\n			{\n				$filter_data[''venue_id''] = $venue[''venue_id''];\n			}\n		}\n		else\n		{\n			$filter_data[''venue_id''] = $data[''venue''];\n		}\n		\n		$filter_data[''role_id''] = $data[''role_id''];\n		$filter_data[''uniform_id''] = $data[''uniform_id''];\n		$filter_data[''payrate_id''] = $data[''payrate_id''];\n		#$filter_data[''payrate_type''] = $data[''payrate_type''];\n		\n		$count = $data[''count''];\n		if ($count < 1)\n		{\n			echo json_encode(array(''ok'' => false, ''error_id'' => ''count''));\n			return;\n		}\n		\n		for($i=0; $i < $count; $i++)\n		{\n			$this->job_shift_model->insert_job_shift($filter_data);\n		}\n		echo json_encode(array(''ok'' => true, ''job_date'' => $filter_data[''job_date'']));\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(129, 43, 'sort_shifts', 'public', 'ajax function to set sort key & value when getting shifts', '', '(POST) ''key''', '[{"attr_name":"return:","attr_desc":"\\t (void)\\n\\t"}]', 'function sort_shifts()\n	{\n		$key = $this->input->post(''key'');\n		$shifts_sort_value = ''asc'';\n		$shifts_sort_key = $this->session->userdata(''shifts_sort_key'');\n		if ($shifts_sort_key == $key)\n		{\n			# Change sort value\n			$shifts_sort_value = $this->session->userdata(''shifts_sort_value'');\n			if ($shifts_sort_value == ''asc'')\n			{\n				$shifts_sort_value = ''desc'';\n			} else\n			{\n				$shifts_sort_value = ''asc'';\n			}\n		}\n		else\n		{\n			# Init sort key & value\n			$shifts_sort_key = $key;\n		}\n		$this->session->set_userdata(''shifts_sort_key'', $shifts_sort_key);\n		$this->session->set_userdata(''shifts_sort_value'', $shifts_sort_value);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(130, 43, 'load_day_shifts', 'public', 'ajax function to load list view of shifts by day', '', '(POST) (int) job_id, (string: YYYY-MM-DD) job_date', '[{"attr_name":"return:","attr_desc":"\\t list view (table) of shifts\\n\\t"}]', 'function load_day_shifts()\n	{\n		$job_id = $this->input->post(''job_id'');\n		# Get all dates of the job\n		$job_dates = $this->job_shift_model->get_job_dates($job_id);\n		\n		# Get selected date from POST request first\n		$date = $this->input->post(''date'');\n		\n		# Then check the session\n		if (!$date)\n		{\n			$date = $this->session->userdata(''job_date'');\n		}\n		\n		# Otherwise, get the very next day\n		if (!$date) {\n			foreach($job_dates as $job_date)\n			{\n				if (strtotime($job_date[''job_date'']) >= strtotime(date(''Y-m-d'', now())))\n				{\n					$date = $job_date[''job_date''];\n					break;\n				}\n			}\n		}\n		\n		$this->session->set_userdata(''job_date'', $date);\n		\n		$data[''total_date''] = count($job_dates);\n		\n		# Get previous and next days\n		$key = 0;\n		foreach($job_dates as $index => $value)\n		{\n			if ($value[''job_date''] == $date)\n			{\n				$key = $index;\n			}\n		}		\n		if ($key == 0) {\n			$right_index = 2;\n			$left_index = 0;\n		} else if ($key == count($job_dates) - 1) {\n			$right_index = $key;\n			$left_index = $key - 2;\n		} else {\n			$right_index = $key + 1;\n			$left_index = $key - 1;\n		}\n		\n		# Optimized job dates array\n		$op_job_dates = array();\n		foreach($job_dates as $index => $value)\n		{\n			if ($index >= $left_index && $index <= $right_index)\n			{\n				$op_job_dates[] = $value;\n			}\n		}\n		\n		$data[''job_id''] = $job_id;\n		$data[''job_dates''] = $op_job_dates;\n		$data[''job_shifts''] = $this->job_shift_model->get_job_shifts($job_id, $date,\n						$this->session->userdata(''shifts_sort_key''),\n						$this->session->userdata(''shifts_sort_value''));\n		$this->load->view(''shifts_day_view'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(131, 43, 'load_month_view', 'public', 'ajax function to set calendar_view to month', '', '(POST) (int) ''date'' timestamp', '[{"attr_name":"return:","attr_desc":"\\t (string) YYYY-MM-DD\\n\\t"}]', 'function load_month_view()\n	{\n		$this->session->set_userdata(''calendar_view'', ''month'');\n		echo date(''Y-m-d'', $this->input->post(''date''));\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(132, 43, 'load_week_view', 'public', 'ajax function to set calendar_view to week', '', '(POST) (int) ''date'' timestamp', '[{"attr_name":"return:","attr_desc":"\\t (string) YYYY-MM-DD\\n\\t"}]', 'function load_week_view()\n	{\n		$this->session->set_userdata(''calendar_view'', ''week'');\n		echo date(''Y-m-d'', $this->input->post(''date''));\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(133, 43, 'load_job_calendar', 'public', 'ajax function to load calendar view (month/week) of shifts', '', '(POST) (int) ''job_id''', '[{"attr_name":"return:","attr_desc":"\\t (view) calendar view\\n\\t"}]', 'function load_job_calendar()\n	{\n		$job_id = $this->input->post(''job_id'');\n		$job_dates = $this->job_shift_model->get_job_dates($job_id);\n		$data[''custom_date''] = now();\n		if ($job_dates)\n		{\n			$data[''custom_date''] = strtotime($job_dates[0][''job_date'']);\n		}\n		if ($this->input->post(''date''))\n		{\n			$data[''custom_date''] = strtotime($this->input->post(''date''));\n		}\n		if ($data[''custom_date''] < now())\n		{\n			$data[''custom_date''] = now();\n		}\n		$data[''job_id''] = $job_id;\n		\n		if (!$this->session->userdata(''calendar_view'') || $this->session->userdata(''calendar_view'') == ''week'') {\n			$this->load->view(''shifts_week_view'', isset($data) ? $data : NULL);	\n		} \n		else if ($this->session->userdata(''calendar_view'') == ''month'') {\n			$out = array();\n			foreach($job_dates as $date)\n			{\n				$out[] = array(\n					''id'' => $this->job_shift_model->count_job_shifts($job_id, $date[''job_date'']),\n					''title'' => $job_id,\n					''url'' => $date[''job_date''],\n					''start'' => strtotime($date[''job_date'']) . ''000'',\n					''end'' => strtotime($date[''job_date'']) . ''000'',\n				);\n			}\n			$data[''events_source''] = json_encode($out);\n			$this->load->view(''shifts_month_view'', isset($data) ? $data : NULL);\n		}\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(134, 43, 'load_shift_staff', 'public', 'ajax function to load view of assign staff to a shift', '', '(via POST) pk: (int) id of the shift', '[{"attr_name":"return:","attr_desc":"\\t (view) form to assign staff to the shift\\n\\t"}]', 'function load_shift_staff()\n	{\n		$shift_id = $this->input->post(''pk'');\n		$shift = $this->job_shift_model->get_job_shift($shift_id);\n		$data[''staff''] = modules::run(''staff/get_staff'', $shift[''staff_id'']);\n		$data[''shift''] = $shift;\n		$this->load->view(''shift_staff'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(135, 43, 'search_staff_for_shift', 'public', 'ajax function to search staffs for a shift', '', '(via POST) query: (string) keyword for staff name', '[{"attr_name":"return:","attr_desc":"\\t (view) of list of searched staffs\\n\\t"}]', 'function search_staff_for_shift()\n	{\n		$query = $this->input->post(''query'');\n		$this->load->model(''staff/staff_model'');\n		$data[''staffs''] = $this->staff_model->search_staffs(array(''keyword'' => $query, ''limit'' => 6));\n		$this->load->view(''staffs_for_shift'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(136, 43, 'update_shift_staff', 'public', 'ajax function to update staff assign / status to the shift', '', '(via POST)', '[{"attr_name":"-","attr_desc":"\\t\\t\\t shift_id\\n\\t"},{"attr_name":"-","attr_desc":"\\t\\t\\t shift_staff_id: (int) id of staff\\n\\t"},{"attr_name":"-","attr_desc":"\\t\\t\\t status: (int) 1 assigned \\/ 2 confirmed \\/ 3 rejected\\n\\t"},{"attr_name":"-","attr_desc":"\\t\\t\\t shift_staff: (string) staff first name and last name\\n\\t"},{"attr_name":"return:","attr_desc":"\\t json encode\\n\\t"}]', 'function update_shift_staff()\n	{\n		$data = $this->input->post();\n		$update_shift_data = array();\n		if ($data[''shift_staff''])\n		{\n			$staff = modules::run(''staff/get_staff'', $data[''shift_staff_id'']);\n			\n			if ($staff)\n			{\n				$update_shift_data = array(\n					''staff_id'' => $data[''shift_staff_id''],\n					''status'' => $data[''status'']\n				);\n			}\n			else {\n				echo json_encode(array(''ok'' => false, ''msg'' => ''Staff not found''));\n				return;\n			}\n		}\n		else {\n			$update_shift_data = array(\n				''staff_id'' => 0,\n				''status'' => 0\n			);\n		}\n		\n		$this->job_shift_model->update_job_shift($data[''shift_id''], $update_shift_data);\n		echo json_encode(array(\n			''ok'' => true, \n			''shift_id'' => $data[''shift_id''], \n			''value'' => ($data[''shift_staff'']) ? $data[''shift_staff''] : ''No Staff Assigned'',\n			''class_name'' => modules::run(''job/status_to_class'', $update_shift_data[''status''])\n		));\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(137, 43, 'load_shift_breaks', 'public', 'ajax function to load all breaks of the shift', '', '(int) shift_id, via POST', '[{"attr_name":"return:","attr_desc":"\\t (view) form with shift break information filled in\\n\\t"}]', 'function load_shift_breaks()\n	{\n		$shift_id = $this->input->post(''pk'');\n		$shift = $this->job_shift_model->get_job_shift($shift_id);\n		$data[''breaks''] = json_decode($shift[''break_time'']);\n		$data[''shift_id''] = $shift_id;\n		$data[''shift''] = $shift;\n		$this->load->view(''shift_breaks'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(138, 43, 'add_shift_break', 'public', 'ajax function to load form view to add new break to the shift', '', '(int) shift_id, via POST', '[{"attr_name":"return:","attr_desc":"\\t (view) form to enter new break for the shift\\n\\t"}]', 'function add_shift_break()\n	{\n		$shift_id = $this->input->post(''pk'');\n		$shift = $this->job_shift_model->get_job_shift($shift_id);\n		$data[''shift''] = $shift;\n		$this->load->view(''shift_add_break'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(139, 43, 'update_job_shift_breaks', 'public', 'ajax function to update breaks of the shift', '', 'two arrays of breaks length and start time (via POST)', '[{"attr_name":"return:","attr_desc":"\\t json encode - if successful {ok: true, shift_id: (int), minutes: int}\\n\\t"},{"attr_name":"","attr_desc":"\\t\\t\\t\\t\\t\\t - if failed\\t{ok: false, number: error_number}\\n\\t"}]', 'function update_job_shift_breaks()\n	{\n		$length = $this->input->post(''break_length'');\n		$start_at = $this->input->post(''break_start_at'');\n		$job_shift = $this->job_shift_model->get_job_shift($this->input->post(''shift_id''));\n		\n		$breaks = array();\n		$total = 0;\n		foreach($length as $index => $value)\n		{\n			if ($value > 0)\n			{\n				$break_time = array(\n					''length'' => $value * 60,\n					''start_at'' => strtotime($start_at[$index])\n				);\n				\n				if ($break_time[''start_at''] <= $job_shift[''start_time''] || $break_time[''start_at''] >=$job_shift[''finish_time''])\n				{\n					echo json_encode(array(''ok'' => false, ''number'' => $index));\n					return;\n				}\n				$total += $value;\n				$breaks[] = $break_time;\n			}\n		}\n		\n		if ($this->job_shift_model->update_job_shift($job_shift[''shift_id''], array(''break_time'' => json_encode($breaks))))\n		{\n			if ($total > 0) {				\n				$minutes = $total . '' mins'';\n				echo json_encode(array(''ok'' => true, ''shift_id'' => $job_shift[''shift_id''],''minutes'' => $minutes));\n			}\n			else\n			{\n				echo json_encode(array(''ok'' => true, ''shift_id'' => $job_shift[''shift_id''],''minutes'' => 0));\n			}\n		}\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(140, 43, 'delete_shifts', 'public', 'ajax function to delete shift(s)', '', 'an array of integers (via POST), which are primary keys of shifts', '[{"attr_name":"return:","attr_desc":"\\t json encode {job_id: (int) $job_id, job_date: (YYYY-MM-DD) $job_date}  \\n\\t"}]', 'function delete_shifts()\n	{\n		$shifts = $this->input->post(''shifts'');\n		$shift = null;\n		$result = array();\n		foreach($shifts as $shift_id)\n		{\n			$shift = $this->job_shift_model->get_job_shift($shift_id);\n			$this->job_shift_model->delete_job_shift($shift_id);\n		}\n		if ($shift)\n		{\n			$result[''job_id''] = $shift[''job_id''];\n			if (modules::run(''job/count_job_shifts'', $shift[''job_id''], strtotime($shift[''job_date''])) > 0)\n			{\n				$result[''job_date''] = $shift[''job_date''];\n			}\n		}		\n		echo json_encode($result);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(141, 43, 'load_shifts_copy', 'public', 'ajax function to load the calendar popup for copying shifts across', '', 'string of shift id 1~2~3~4 (via GET)', '[{"attr_name":"return:","attr_desc":"\\t calendar view\\n\\t"}]', 'function load_shifts_copy($s = '''')\n	{\n		$shifts = explode(''~'', $s);\n		\n		$data[''shift''] = $this->job_shift_model->get_job_shift($shifts[0]);\n		$data[''shifts''] = $shifts;		\n		$this->load->view(''shifts_copy'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(142, 43, 'update_selected_day', 'public', 'ajax function to update selected day (to the sessions of array of all selected days) for copying shift function', '', 'null', '[{"attr_name":"return:","attr_desc":"\\t json encode {success: true\\/false, msg: ''''}\\n\\t"}]', 'function update_selected_day()\n	{\n		$ts = $this->input->post(''ts'');\n		$all_ts = $this->session->userdata(''all_ts'');\n		if (!$all_ts) {\n			$all_ts = array();\n		}\n		$key = array_search($ts, $all_ts);\n		if ($key !== false) {\n			unset($all_ts[$key]);\n		}\n		else\n		{\n			if ($ts > now())\n			{\n				$all_ts[] = $ts;\n			}\n			else\n			{				\n				echo json_encode(array(''success'' => false, ''msg'' => ''Cannot copy to date in the past''));\n				return;\n			}\n		}\n		\n		$this->session->set_userdata(''all_ts'', $all_ts);\n		echo json_encode(array(''success'' => true));	\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(143, 43, 'clear_selected_days', 'public', 'ajax function to clear session of selected days for copy', '', '(none)', '[{"attr_name":"return:","attr_desc":"\\t (void)\\n\\t"}]', 'function clear_selected_days()\n	{\n		$this->session->unset_userdata(''all_ts'');\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(144, 43, 'copy_selected_days', 'public', 'ajax function to copy selected shifts to selected days', '', 'array of shift id', '[{"attr_name":"return:","attr_desc":"\\t json encode {success: (boolean), msg: (string)}\\n\\t"}]', 'function copy_selected_days()\n	{\n		$all_ts = $this->session->userdata(''all_ts'');\n		if ($all_ts)\n		{\n			$shifts = $this->input->post(''shifts'');\n			foreach($all_ts as $ts)\n			{\n				foreach($shifts as $shift_id)\n				{\n					$shift = $this->job_shift_model->get_job_shift($shift_id);\n					$new_shift = $shift;\n					unset($new_shift[''shift_id'']);\n					unset($new_shift[''created_on'']);\n					unset($new_shift[''modified_on'']);\n					$new_shift[''job_date''] = date(''Y-m-d'', $ts);\n					$start_time = strtotime(date(''Y-m-d'', $ts) . '' '' . date(''H:i'', $shift[''start_time'']));\n					$finish_time = $start_time + $shift[''finish_time''] - $shift[''start_time''];\n					$new_shift[''start_time''] = $start_time;\n					$new_shift[''finish_time''] = $finish_time;\n					\n					$breaks = json_decode($shift[''break_time'']);\n					$new_breaks = array();\n					foreach($breaks as $break)\n					{\n						$new_breaks[] = array(\n							''length'' => $break->length,\n							''start_at'' => $start_time + $break->start_at - $shift[''start_time'']\n						);\n					}\n					$new_shift[''break_time''] = json_encode($new_breaks);\n					$this->job_shift_model->insert_job_shift($new_shift);\n				}				\n			}\n			$this->session->unset_userdata(''all_ts'');\n			echo json_encode(array(''success'' => true));\n		}\n		else\n		{\n			echo json_encode(array(''success'' => false, ''msg'' => ''No day selected''));\n		}		\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
