(148, 46, 'count_job_shifts', 'public', 'function to count number of shifts in a job, optionally in a specified date', '', '(int) $job_id, (int - timestamp) $job_date', '[{"attr_name":"return:","attr_desc":"\\t (int)\\n\\t"}]', 'function count_job_shifts($job_id, $job_date = null, $status = null)\n	{\n		if ($job_date)\n		{\n			$job_date = date(''Y-m-d'', $job_date);\n		}\n		echo $this->job_shift_model->count_job_shifts($job_id, $job_date, $status);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(149, 46, 'get_day_shifts', 'public', 'function to get list of shifts in a day', '', '(int) $job_id, (int - timestamp) $job_date', '[{"attr_name":"return:","attr_desc":"\\t (array)\\n\\t"}]', 'function get_day_shifts($job_id, $job_date)\n	{\n		$job_date = date(''Y-m-d'', $job_date);\n		$shifts = $this->job_shift_model->get_job_shifts($job_id, $job_date);\n		$ids = array();\n		foreach($shifts as $shift)\n		{\n			$ids[] = $shift[''shift_id''];\n		}\n		return $ids;\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
