(151, 49, 'get_shift_by_year_and_month', 'public', 'Function to get shifts based on their status', '', 'int month, int year', '[{"attr_name":"return","attr_desc":"\\t Returns array of avaliable roles\\n\\t"}]', 'function get_shift_by_year_and_month($month,$year,$status = 0)\n	{		\n		$sql = "select job_date,count(*) as total_shifts from job_shifts where month(job_date) = ''".$month."'' and year(job_date) = ''".$year."''";\n		if($status == ''all''){\n			$sql .= " and status != -2 and status != -1";	\n		}else{\n			$sql .= " and status = ".$status;	\n		}\n		$sql .= " group by job_date order by job_date asc";\n		$shifts = $this->db->query($sql)->result();\n		return $shifts;\n		\n	}\n}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
