(156, 55, 'list_staffs', 'public', 'ajax function to get the list of staff with batched timesheets', '', '(void)', '[{"attr_name":"return:","attr_desc":"\\t (html) main layout of list pay runs \\n\\t"}]', 'function list_staffs() {\n		$data[''staffs''] = $this->payrun_model->get_staffs();\n		$this->load->view(''source/staffs_list_view'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
