(158, 55, 'set_filter', 'public', 'ajax function to set filter for list pay runs and save to sessions', '', '(via POST)', '[{"attr_name":"-","attr_desc":"\\t\\t name\\n\\t"},{"attr_name":"-","attr_desc":"\\t\\t value\\n\\t"},{"attr_name":"return:","attr_desc":"\\t (void)\\n\\t"}]', 'function set_filter() {\n		$this->session->set_userdata(''prf_'' . $this->input->post(''name''), $this->input->post(''value''));\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(159, 55, 'row_timesheets_staff', 'public', 'ajax function to display the row (tr) content of batched staff', '', '(via POST)', '[{"attr_name":"-","attr_desc":"\\t\\t (int) user_id\\n\\t"},{"attr_name":"-","attr_desc":"\\t\\t (boolean) expanded\\n\\t"},{"attr_name":"return:","attr_desc":"\\t (html) the row (tr) of batched staff\\n\\t"}]', 'function row_timesheets_staff() {\n		echo modules::run(''payrun/row_batched_staff'', $this->input->post(''user_id''), $this->input->post(''expanded''));\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(160, 55, 'row_timesheet', 'public', 'ajax function to display the row (tr) content of single timesheet', '', '(via POST)', '[{"attr_name":"-","attr_desc":"\\t\\t (int) timesheet_id\\n\\t"},{"attr_name":"-","attr_desc":"\\t\\t (int) user_id\\n\\t"},{"attr_name":"return:","attr_desc":"\\t (html) the row (tr) of single timesheet\\n\\t"}]', 'function row_timesheets_staff() {\n		echo modules::run(''payrun/row_batched_staff'', $this->input->post(''user_id''), $this->input->post(''expanded''));\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
