(162, 56, 'main_view', 'public', 'load the main layout of the payrun page', '', '(void)', '[{"attr_name":"return:","attr_desc":"\\t (void) load main layout of the payrun page\\n\\t"}]', 'function main_view() {\n		$this->load->view(''main_view'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(163, 56, 'row_batched_staff', 'public', 'load the row (tr) of batched timesheets of a specified staff', '', '(int) $user_id', '[{"attr_name":"(boolean)","attr_desc":"\\t\\t\\t $expanded (default: false)\\n\\t"},{"attr_name":"return:","attr_desc":"\\t (html) row (tr) content of batched timesheets of a specified staff\\n\\t"}]', 'function row_batched_staff($user_id, $expanded = false) {\n		$timesheets = $this->payrun_model->get_staff_timesheets($user_id);\n		$data[''staff_timesheets''] = $timesheets;\n		$data[''staff''] = $this->staff_model->get_staff($user_id);\n		$data[''expanded''] = $expanded;\n		$checked = false;\n		$payrun_timesheets = $this->session->userdata(''payrun_timesheets'');\n		if (is_array($payrun_timesheets)) {\n			foreach($timesheets as $timesheet) {\n				if (in_array($timesheet[''timesheet_id''], $payrun_timesheets)) {\n					$checked = true;\n				}\n			}\n		}	\n		\n		$data[''checked''] = $checked;\n		return $this->load->view(''batched_staff_row'', isset($data) ? $data : NULL, true);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(164, 56, 'row_timesheet', 'public', 'load the row (tr) of single timesheet', '', '(int) $timesheet_id', '[{"attr_name":"(int)","attr_desc":"\\t\\t\\t $user_id\\n\\t"},{"attr_name":"return:","attr_desc":"\\t (html) row (tr) content of single timesheet\\n\\t"}]', 'function row_timesheet($timesheet_id, $user_id) {\n		$data[''timesheet''] = $this->payrun_model->get_timesheet($timesheet_id);\n		$data[''user_id''] = $user_id;\n		$payrun_timesheets = $this->session->userdata(''payrun_timesheets'');\n		$checked = false;\n		if (is_array($payrun_timesheets)) {\n			if (in_array($timesheet_id, $payrun_timesheets)) {\n				$checked = true;\n			}\n		}\n		$data[''checked''] = $checked;\n		$this->load->view(''timesheet_row'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(165, 56, 'row_timesheets_staff', 'public', 'load the rows (tr) of all timesheets of a specified staff', '', '(int) $user_id', '[{"attr_name":"return:","attr_desc":"\\t (html) rows (tr) of all timesheets of a specified staff \\n\\t"}]', 'function row_timesheets_staff($user_id) \n	{\n		$timesheets = $this->payrun_model->get_staff_timesheets($user_id);\n		foreach($timesheets as $timesheet) {\n			$this->row_timesheet($timesheet[''timesheet_id''], $user_id);\n		}\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(166, 56, 'menu_dropdown', 'public', 'generate the dropdown menu of pay run', '', '(string) $id', '[{"attr_name":"(string)","attr_desc":"\\t\\t\\t $label\\n\\t"},{"attr_name":"return:","attr_desc":"\\t (html) dropdown menu of pay run filter\\n\\t"}]', 'function menu_dropdown($id, $label) {\n		$data = array(\n			array(''value'' => '''', ''label'' => ''Any''),\n			array(''value'' => PAYRUN_READY, ''label'' => ''Yes'')\n		);\n		return modules::run(''common/menu_dropdown'', $data, $id, $label);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(167, 56, 'menu_dropdown_actions', 'public', 'generate the dropdown menu of actions', '', '(string) $id', '[{"attr_name":"(string)","attr_desc":"\\t\\t\\t $label\\n\\t"},{"attr_name":"return:","attr_desc":"\\t (html) dropdown menu of actions\\n\\t"}]', 'function menu_dropdown_actions($id, $label) {\n		$data = array(\n			array(''value'' => ''process'', ''label'' => ''Set Yes for Pay Run''),\n			array(''value'' => ''unprocess'', ''label'' => ''Set No for Pay Run''),\n			array(''value'' => ''archive'', ''label'' => ''Set as Paid & Archive''),\n			array(''value'' => ''revert'', ''label'' => ''Revert Selected'')\n		);\n		return modules::run(''common/menu_dropdown'', $data, $id, $label);\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(168, 57, 'count_staff', 'public', 'count the number of staffs ready for pay run', '', '(int) $tfn (1: TFN, 2: ABN)', '[{"attr_name":"return:","attr_desc":"\\t (int) number of staffs\\n\\t"}]', 'function count_staff($tfn=0) {\n		$sql = "SELECT u.* FROM `job_shift_timesheets` j\n					LEFT JOIN `user_staffs` u ON j.staff_id = u.user_id\n					WHERE j.status = " . TIMESHEET_BATCHED . " \n					AND j.status_payrun_staff = " . PAYRUN_READY . " \n					AND u.f_employed = " . $tfn . " GROUP BY j.staff_id";\n		$query = $this->db->query($sql);\n		return $query->num_rows();\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(169, 57, 'get_total_amount', 'public', 'get total amount ready for staff pay run', '', '(int) $tfn (1: TFN, 2: ABN)', '[{"attr_name":"return:","attr_desc":"\\t (decimal) total amount to pay\\n\\t"}]', 'function get_total_amount($tfn=0) {\n		$sql = "SELECT sum(j.total_amount_staff) as `total` FROM `job_shift_timesheets` j\n					LEFT JOIN `user_staffs` u ON j.staff_id = u.user_id\n					WHERE j.status = " . TIMESHEET_BATCHED . " \n					AND j.status_payrun_staff = " . PAYRUN_READY . " \n					AND u.f_employed = " . $tfn;\n		$query = $this->db->query($sql);\n		$result = $query->first_row(''array'');\n		if ($result && isset($result[''total''])) {\n			return $result[''total''];\n		}\n		return 0;\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(170, 57, 'get_staffs', 'public', 'get all staffs have timesheets need to be paid', '', '(void)', '[{"attr_name":"return:","attr_desc":"\\t (array) of staff object\\n\\t"}]', 'function get_staffs() {\n		$sql = "SELECT u.* FROM `job_shift_timesheets` j\n					LEFT JOIN `users` u ON j.staff_id = u.user_id\n					LEFT JOIN `user_staffs` s ON j.staff_id = s.user_id\n					WHERE j.status = " . TIMESHEET_BATCHED;\n		\n		$prf_state = $this->session->userdata(''prf_state'');\n		if ($prf_state) {\n			$sql .= " AND u.state = ''" . $prf_state . "''";\n		}\n		$prf_tfn = $this->session->userdata(''prf_tfn'');\n		if ($prf_tfn != "") {\n			$sql .= " AND s.f_employed = ''" . $prf_tfn . "''";\n		}\n		$prf_status = $this->session->userdata(''prf_status'');\n		if ($prf_status != "") {\n			$sql .= " AND j.status_payrun_staff = ''" . $prf_status . "''";\n		}\n			\n		$sql .= " GROUP BY j.staff_id";\n		$query = $this->db->query($sql);\n		return $query->result_array();\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(171, 57, 'get_timesheet', 'public', 'get batched timesheet', '', '(int) $timesheet_id', '[{"attr_name":"return:","attr_desc":"\\t (obj) timesheet object\\n\\t"}]', 'function get_timesheet($timesheet_id) {\n		$this->db->where(''timesheet_id'', $timesheet_id);\n		$this->db->where(''status'', TIMESHEET_BATCHED);\n		$query = $this->db->get(''job_shift_timesheets'');\n		return $query->first_row(''array'');\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(172, 57, 'get_staff_timesheets', 'public', 'get all timesheets by a staff', '', '(int) $user_id', '[{"attr_name":"return:","attr_desc":"\\t (array) of timesheet objects\\n\\t"}]', 'function get_staff_timesheets($user_id)\n	{\n		$this->db->where(''staff_id'', $user_id);\n		$this->db->where(''status'', TIMESHEET_BATCHED);\n		\n		$prf_status = $this->session->userdata(''prf_status'');\n		if ($prf_status != "") {\n			$this->db->where(''status_payrun_staff'', $prf_status);\n		}\n		\n		$query = $this->db->get(''job_shift_timesheets'');\n		return $query->result_array();\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(173, 57, 'process_staff_payruns', 'public', 'set all timesheets of a staff to be ready for pay run', '', '(int) $staff_id', '[{"attr_name":"return:","attr_desc":"\\t (boolean)\\n\\t"}]', 'function process_staff_payruns($staff_id) \n	{\n		$this->db->where(''staff_id'', $staff_id);\n		return $this->db->update(''job_shift_timesheets'', array(''status_payrun_staff'' => PAYRUN_READY));\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(174, 57, 'process_payrun', 'public', 'set a timesheet to be ready for pay run', '', '(int) $timesheet_id', '[{"attr_name":"return:","attr_desc":"\\t (boolean)\\n\\t"}]', 'function process_payrun($timesheet_id)\n	{\n		$this->db->where(''timesheet_id'', $timesheet_id);\n		return $this->db->update(''job_shift_timesheets'', array(''status_payrun_staff'' => PAYRUN_READY));\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(175, 57, 'unprocess_staff_payruns', 'public', 'set all timesheets of a staff to not be ready for pay run', '', '(int) $staff_id', '[{"attr_name":"return:","attr_desc":"\\t (boolean)\\n\\t"}]', 'function unprocess_staff_payruns($staff_id)\n	{\n		$this->db->where(''staff_id'', $staff_id);\n		return $this->db->update(''job_shift_timesheets'', array(''status_payrun_staff'' => PAYRUN_PENDING));\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(176, 57, 'unprocess_payrun', 'public', 'set a timesheet to not be ready for pay run', '', '(int) $timesheet_id', '[{"attr_name":"return:","attr_desc":"\\t (boolean)\\n\\t"}]', 'function unprocess_payrun($timesheet_id)\n	{\n		$this->db->where(''timesheet_id'', $timesheet_id);\n		return $this->db->update(''job_shift_timesheets'', array(''status_payrun_staff'' => PAYRUN_PENDING));\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(177, 57, 'revert_staff_payruns', 'public', 'revert all timesheets of a staff to previous status (not batched)', '', '(int) $staff_id', '[{"attr_name":"return:","attr_desc":"\\t (boolean)\\n\\t"}]', 'function revert_staff_payruns($staff_id)\n	{\n		$this->db->where(''staff_id'', $staff_id);\n		return $this->db->update(''job_shift_timesheets'', array(''status'' => TIMESHEET_APPROVED));\n	}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
(178, 57, 'revert_payrun', 'public', 'revert a timesheet to previous status (not batched)', '', '(int) $timesheet_id', '[{"attr_name":"return:","attr_desc":"\\t (boolean)\\n\\t"}]', 'function revert_payrun($timesheet_id)\n	{\n		$this->db->where(''timesheet_id'', $timesheet_id);\n		return $this->db->update(''job_shift_timesheets'', array(''status'' => TIMESHEET_APPROVED));\n	}\n}', '2014-02-28 05:18:31', '0000-00-00 00:00:00'),
