(202, 76, 'get_staffs_roles', 'public', 'Displayes all the available roles in the system and the roles that has been asigned to the staff.', '', 'Post data - sort parameter and user id (staff id)', '[{"attr_name":"return","attr_desc":"\\t Lists all roles with roles that has been assigned to staffs checked\\n\\t"}]', 'function get_staffs_roles()\n	{\n		$params = $this->input->post(''params'',true);\n		$data[''roles''] = $this->role_model->get_roles($params);\n		$data[''params''] = $params;\n		$this->load->view(''ajax_list_roles_staff_profile'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
