(204, 76, 'add_location', 'public', 'ajax function to add location of staff', '', '(via POST)', '[{"attr_name":"-","attr_desc":"\\t\\t\\t (int) location_parent_id\\n\\t"},{"attr_name":"-","attr_desc":"\\t\\t\\t (int) location_id\\n\\t"},{"attr_name":"-","attr_desc":"\\t\\t\\t (int) user_id\\n\\t"},{"attr_name":"return:","attr_desc":"\\t json encode array {ok: (true\\/false)}\\n\\t"}]', 'function add_location() {		\n		$parent_id = $this->input->post(''location_parent_id'');\n		if (!$parent_id) {\n			echo json_encode(array(''ok'' => false));\n			return;\n		}\n		$location_id = $this->input->post(''location_id'');\n		\n		$data = array();\n		$location = array();		\n		\n		if (!$location_id) { # Select all locations within location_parent_id\n			$all = modules::run(''attribute/location/get_locations'', $parent_id);\n			foreach($all as $a) {\n				$location[] = $a[''location_id''];\n			}\n		}\n		else\n		{\n			$location[] = $location_id;\n		}\n		$data[$parent_id] = $location;\n		\n		\n		# Now merging with current locations data\n		$staff = $this->staff_model->get_staff($this->input->post(''user_id''));\n		$locations = json_decode($staff[''locations'']);\n		\n		if (count($locations) > 0) foreach($locations as $o_parent_id => $o_childrens)\n		{\n			if ($o_parent_id != $parent_id) # Adding old parent locations\n			{\n				$data[$o_parent_id] = $o_childrens;\n			}\n			else\n			{\n				if (!$location_id)\n				{\n					$data[$parent_id] = $location;\n				}\n				else if (!in_array($location_id, $o_childrens))\n				{\n					$data[$parent_id] = array_merge($o_childrens, $location);\n				}\n				else\n				{\n					$data[$parent_id] = $o_childrens;\n				}\n			}\n		}\n		\n		$this->staff_model->update_staff($staff[''user_id''], array(''locations'' => json_encode($data)));\n		echo json_encode(array(''ok'' => true));\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(205, 76, 'remove_location', 'public', 'ajax function to remove location of staff', '', '(via POST)', '[{"attr_name":"-","attr_desc":"\\t\\t\\t (int) parent_id\\n\\t"},{"attr_name":"-","attr_desc":"\\t\\t\\t (int) location_id\\n\\t"},{"attr_name":"-","attr_desc":"\\t\\t\\t (int) user_id\\n\\t"},{"attr_name":"return:","attr_desc":"\\t (void)\\n\\t"}]', 'function remove_location() {\n		var_dump($this->input->post());\n		$staff = $this->staff_model->get_staff($this->input->post(''user_id''));\n		$parent_id = $this->input->post(''parent_id'');\n		$location_id = $this->input->post(''location_id'');\n		$locations = json_decode($staff[''locations'']);\n		\n		$data = array();\n		if (!$location_id)\n		{\n			foreach($locations as $o_parent_id => $childrens)\n			{\n				if ($o_parent_id != $parent_id)\n				{\n					$data[$o_parent_id] = $childrens;\n				}\n			}\n		}\n		else\n		{\n			foreach($locations as $o_parent_id => $childrens)\n			{\n				$new_locations = array();\n				foreach($childrens as $child)\n				{\n					if ($child != $location_id) {\n						$new_locations[] = $child;\n					}\n				}\n				$data[$o_parent_id] = $new_locations;\n			}\n			\n		}\n		$this->staff_model->update_staff($staff[''user_id''], array(''locations'' => json_encode($data)));\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(206, 76, 'load_locations', 'public', 'ajax function to load staff locations view', '', '(via POST) (int) user_id', '[{"attr_name":"return:","attr_desc":"\\t (view) staff locations\\n\\t"}]', 'function load_locations()\n	{\n		$staff = $this->staff_model->get_staff($this->input->post(''user_id''));\n		$data[''staff''] = $staff;\n		$data[''locations''] = json_decode($staff[''locations'']);\n		$this->load->view(''staff_locations'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(207, 76, 'load_availability', 'public', 'ajax function to load staff availability view. If staff doesn''t have availability, the funcition will create avaiability record as available all days all times', '', '(via POST) (int) user_id', '[{"attr_name":"return:","attr_desc":"\\t (view) staff availability\\n\\t"}]', 'function load_availability()\n	{\n		$user_id = $this->input->post(''user_id'');\n		$availability = $this->staff_model->get_availability($user_id);\n		if(count($availability)<=0){\n			$this->initiate_availability($user_id);\n			$availability = $this->staff_model->get_availability($user_id);\n		}\n		$data[''user_id''] = $user_id;\n		$data[''availability''] = $availability;\n		$this->load->view(''staff/availability_table_view'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(208, 76, 'initiate_availability', 'public', 'initiate value of staff availability time, if there is no data yet. It will be initiated as available all days and all times', '', '(via parameter) (int) user_id', '', 'function initiate_availability($user_id)\n	{\n		$values = '''';			\n		for($day=1; $day <=7; $day++) {\n			for($hour=0; $hour <=23; $hour++) {\n				$values .= ''(''.$user_id.'',''.$day.'',''.$hour.'',1),''; 		\n			}\n		}  \n		$values = rtrim($values,'','');\n		$sql = "INSERT INTO `user_staff_availability` (`user_id`, `day`, `hour`, `value`) VALUES ".$values;\n		$this->db->query($sql);\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(209, 76, 'update_availability', 'public', 'update value of staff availability time', '', '(via POST) (int) user_id,name,value', '', 'function update_availability()\n	{\n		$value = $this->input->post(''value_avail'');\n		$name = $this->input->post(''name'');\n		$fields = explode(''-'', $name);		\n		$day = $fields[1];\n		$hour = $fields[2];\n		$this->staff_model->update_available_data($this->input->post(''user_id''), $day, $hour, $value);\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(210, 76, 'get_staffs_roles', 'public', 'Displayes all the available groups in the system and the roles that has been asigned to the staff.', '', 'Post data - sort parameter and user id (staff id)', '[{"attr_name":"return","attr_desc":"\\t Lists all roles with roles that has been assigned to staffs checked\\n\\t"}]', 'function get_staffs_roles()\n	{\n		$params = $this->input->post(''params'',true);\n		$data[''roles''] = $this->role_model->get_roles($params);\n		$data[''params''] = $params;\n		$this->load->view(''ajax_list_roles_staff_profile'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(211, 76, 'update_groups', 'public', 'ajax function to add or delete groups', '', '(via POST)', '[{"attr_name":"-","attr_desc":"\\t\\t\\t (int) user_id\\n\\t"},{"attr_name":"-","attr_desc":"\\t\\t\\t (int) group_id\\n\\t"}]', 'function update_groups()\n	{\n		$user_id = $this->input->post(''staff_id'');\n		$group_id = $this->input->post(''group_id'');\n		if($this->staff_model->update_staff_group($user_id,$group_id)){\n			echo ''success'';	\n		}\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(212, 76, 'load_picture', 'public', 'show the profile picture and gallery', '', '(via POST) (int) user_id', '', 'function load_picture()\n	{\n		$user_id = $this->input->post(''user_id'',true);\n		$photos = $this->staff_model->get_all_photos($user_id);\n		$hero_photo = $this->staff_model->get_hero($user_id);	\n		$data[''hero_photo''] = $hero_photo;\n		$data[''photos''] = $photos;\n		$data[''user_id''] = $user_id;\n		//error_reporting(E_ALL);\n		\n		$this->load->view(''staff/list_photo'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(213, 76, 'update_custom_attributes', 'public', 'Update custom attribute of a staff', '', '(via POST) custom attributes data', '', 'function update_custom_attributes()\n	{\n		$post_data = $this->input->post();\n		$user_id = $this->input->post(''user_staff_id'',true);\n		//delete existing data\n		$this->staff_model->delete_staff_custom_attributes($user_id);\n		foreach($post_data as $key => $val){\n			if($key != ''user_staff_id''){\n				//for checkbox and multi select \n				if(modules::run(''formbuilder/has_multiple_value'',$key)){\n					$custom_attr = array(\n										''user_id'' => $user_id,\n										''attribute_name'' => $key,\n										''attributes'' => json_encode($val)\n									);\n				}else{\n					$custom_attr = array(\n										''user_id'' => $user_id,\n										''attribute_name'' => $key,\n										''attributes'' => $val\n									);	\n				}\n				$this->staff_model->insert_staff_custom_attributes($custom_attr);\n			}\n		}\n		echo ''success'';\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(214, 76, 'set_hero', 'public', 'Set hero image', '', '(via POST) user_staff_picture_id', '', 'function set_hero_photo()\n	{\n		$user_staff_picture_id = $this->input->post(''user_staff_picture_id'',true);\n		$user_id = $this->input->post(''user_id'',true);\n		$this->staff_model->uset_hero($user_id);\n		echo $this->staff_model->update_user_staff_picture($user_staff_picture_id,array(''hero''=> 1));\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(215, 76, 'set_hero', 'public', 'Set hero image', '', '(via POST) user id', '', 'function set_hero_photo()\n	{\n		$user_staff_picture_id = $this->input->post(''user_staff_picture_id'',true);\n		$user_id = $this->input->post(''user_id'',true);\n		$this->staff_model->uset_hero($user_id);\n		echo $this->staff_model->update_user_staff_picture($user_staff_picture_id,array(''hero''=> 1));\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(216, 76, 'reload_staff_edit_page_avatar', 'public', 'Reloads profile avatars whenever an update to profile image is made', '', '(via POST) user id', '[{"attr_name":"return:","attr_desc":"\\t Reloads staff profile avatars\\n\\t"}]', 'function reload_staff_edit_page_avatar()\n	{\n		$staff_user_id = $this->input->post(''user_id'',true);\n		echo modules::run(''common/profile_picture'','''',$staff_user_id);\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(217, 76, 'delete_photo', 'public', 'Delete staff photo', '', '(via POST) photo id', '', 'function delete_photo()\n	{\n		$photo_id = $this->input->post(''photo_id'',true);\n		$photo = $this->staff_model->get_user_staff_photo_by_photo_id($photo_id);\n		$path = ''./uploads/staff/profile/''.md5($photo->user_id);\n		$sub_folders = array(''thumbnail'',''thumbnail2'');\n		$file_name = $photo->name;\n		modules::run(''staff/delete_files'',$path,$file_name,$sub_folders);\n		if($this->staff_model->delete_photo($photo_id)){\n			echo ''success'';\n		}else{\n			echo ''failed'';\n		}\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(218, 76, 'update_ratings', 'public', 'Update rating', '', '(via POST) field name of the rating to populate back, user id, ajax reload container, new rating', '', 'function update_ratings()\n	{\n		$field_name = $this->input->post(''field_name'',true);\n		$user_id = $this->input->post(''user_id'',true);\n		$ajax_reload_container = $this->input->post(''ajax_reload_container'',true);\n		$selector = $this->input->post(''selector'',true);\n		$new_rating = $this->input->post(''new_rating'',true);\n		$data = array(''rating'' => $new_rating);\n		$this->staff_model->update_staff($user_id,$data);\n		\n		echo modules::run(''common/field_rating'', $field_name, $new_rating,$selector,$ajax_reload_container,$user_id,true,false);\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(219, 76, 'delete_staff', 'public', 'Delete single staff', '', '(via POST) user id', '', 'function delete_staff()\n	{\n		$user_id = $this->input->post(''user_id'',true);\n		return $this->user_model->update_user($user_id,array(''status'' => 2));	\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
