(221, 76, 'update_rating_multi_staffs', 'public', 'Update rating for multiple staff', '', '(via POST) user id and new rating', '', 'function update_rating_multi_staffs()\n	{\n		$user_ids = $this->input->post(''user_staff_selected_user_id'',true);\n		$new_rating = $this->input->post(''multi_rating'',true);\n		return $this->staff_model->update_rating_multi_staffs(implode('','',$user_ids),$new_rating);\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(222, 76, 'update_status_multi_staffs', 'public', 'Update status for multiple staff', '', '(via POST) user id and new stats', '', 'function update_status_multi_staffs()\n	{\n		$user_ids = $this->input->post(''user_staff_selected_user_id'',true);\n		$new_status = $this->input->post(''new_multi_status'',true);\n		return $this->user_model->update_status_multi_users(implode('','',$user_ids),$new_status);\n	}\n}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
