(224, 77, 'search_staffs', 'private', 'function to load search staffs form', '', '(POST) array of search parameters', '[{"attr_name":"return:","attr_desc":"\\t (void) load search staffs form\\n\\t"}]', 'private function search_staffs()\n	{\n		$data[''weekdays''] = array(\n							array(''value'' => 1, ''label'' => ''Monday''),\n							array(''value'' => 2, ''label'' => ''Tuesday''),\n							array(''value'' => 3, ''label'' => ''Wednesday''),\n							array(''value'' => 4, ''label'' => ''Thursday''),\n							array(''value'' => 5, ''label'' => ''Friday''),\n							array(''value'' => 6, ''label'' => ''Saturday''),\n							array(''value'' => 7, ''label'' => ''Sunday'')\n							);\n		$data[''age_groups''] = array(\n							array(''value'' => ''0-17'', ''label'' => ''Under 18 Years Old''),\n							array(''value'' => ''18-25'', ''label'' => ''18 - 25 Years Old''),\n							array(''value'' => ''26-35'', ''label'' => ''26 - 35 Years Old''),\n							array(''value'' => ''36-100'', ''label'' => ''35+ Years Old'')\n							);\n		$this->load->view(''search_form'', isset($data) ? $data : NULL);\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(225, 77, 'field_select_status', 'public', 'custom select staff status field', '', '- $field_name', '[{"attr_name":"-","attr_desc":"\\t\\t\\t $field_value (optional)\\n\\t"},{"attr_name":"-","attr_desc":"\\t\\t\\t $size (optional)\\n\\t"},{"attr_name":"return:","attr_desc":"\\t custom select staff status field\\n\\t"}]', 'function field_select_status($field_name, $field_value=null, $size=null)\n	{\n		$array = array(\n			array(''value'' => STAFF_ACTIVE, ''label'' => ''Active''),\n			array(''value'' => STAFF_PENDING, ''label'' => ''Pending''),\n			array(''value'' => STAFF_INACTIVE, ''label'' => ''Inactive'')\n		);\n		\n		return modules::run(''common/field_select'', $array, $field_name, $field_value, $size);\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(226, 77, 'check_staff_has_role', 'public', 'Checks if a staff has been assigned this role.', '', 'null', '[{"attr_name":"return","attr_desc":"\\t Returns is based on the return_bool value. If it is true or empty it return true or false, if it is false it \\n\\t"}]', 'function check_staff_has_role($staff_user_id,$role_id)\n	{\n		$has = $this->staff_model->staff_has_role($staff_user_id,$role_id);\n		if($has){\n			return true;\n		}else{\n			return false;\n		}\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(227, 77, 'check_staff_has_role', 'public', 'Checks if a staff has been assigned this role.', '', 'null', '[{"attr_name":"return","attr_desc":"\\t Returns is based on the return_bool value. If it is true or empty it return true or false, if it is false it \\n\\t"}]', 'function check_staff_has_role($staff_user_id,$role_id)\n	{\n		$has = $this->staff_model->staff_has_role($staff_user_id,$role_id);\n		if($has){\n			return true;\n		}else{\n			return false;\n		}\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(228, 77, 'get_availability_data', 'public', 'function to return value of availability of user based on day and time', '', '(via parameter) (int) user_id, day, hour', '[{"attr_name":"return:","attr_desc":"\\t (int) value of staff availability\\n\\t"}]', 'function get_availability_data($user_id, $day, $hour)\n	{\n		return $this->staff_model->get_availability_data($user_id, $day, $hour);\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(229, 77, 'get_total_staffs', 'public', 'Returns total staff based on status. If status is not passed it returns the total staffs', '', 'string, int status', '[{"attr_name":"return:","attr_desc":"\\t (int) total staff\\n\\t"}]', 'function get_total_staffs($status='''')\n	{\n		return $this->staff_model->get_total_staffs_count($status);\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(230, 77, 'get_staff_custom_attribute', 'public', 'Get custom attribute of a staff member if it has been set', '', '(int) user id of staff, (string) the name of the custom attribute', '[{"attr_name":"return:","attr_desc":"\\t (int) total staff\\n\\t"}]', 'function get_staff_custom_attribute($staff_user_id,$attribute_name)\n	{\n		//first get attribute type\n		$has_multiple_value = modules::run(''formbuilder/has_multiple_value'',$attribute_name);\n		$staff_attribute = $this->staff_model->get_staff_custom_attribute($staff_user_id,$attribute_name);\n		$data[''has_multi''] = false;\n		if($staff_attribute){\n			if($has_multiple_value){\n				$data[''attributes''] = json_decode($staff_attribute->attributes);\n				$data[''staffs_custom_attributes_id''] = $staff_attribute->staffs_custom_attributes_id;\n				$data[''has_multi''] = true;\n			}else{\n				$data[''attributes''] = $staff_attribute->attributes;	\n				$data[''staffs_custom_attributes_id''] = $staff_attribute->staffs_custom_attributes_id;\n			}\n		}else{\n			$data[''attributes''] = '''';\n			$data[''staffs_custom_attributes_id''] = '''';	\n		}\n		return $data;\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(231, 77, 'upload_custom_document', 'public', 'Uploads document that has been created using the custom attributes.', '', '(file) data is posted', '[{"attr_name":"return:","attr_desc":"\\t redirect to staff profile \\n\\t"}]', 'function upload_custom_document()\n	{\n		$user_staff_id = $this->input->post(''user_staff_id'',true);\n		$salt = ''custom_files''.$user_staff_id;\n		$file_path = ''./uploads/staff/custom_attributes'';\n		//create folder\n		$folder_name = $this->_create_folders(''./uploads/staff/custom_attributes'',$salt);\n		$this->load->library(''upload'');\n		foreach($_FILES as $key => $val){\n			if ($_FILES[$key][''name'']){\n			//image upload\n			$config[''upload_path''] = $file_path."/".$folder_name;	\n			$config[''allowed_types''] = ''gif|jpg|png|jpeg|doc|docx|pdf'';\n			$config[''max_size'']	= ''4096''; // 4 MB\n			$config[''max_width'']  = ''4000'';\n			$config[''max_height'']  = ''4000'';\n			$config[''overwrite''] = FALSE;\n			$config[''remove_space''] = TRUE;\n			\n			$this->upload->initialize($config);\n			if (!$this->upload->do_upload($key)) {\n					//image upload filed abort file upload\n					$data[''upload_error''] = $this->upload->display_errors();\n					$valid_upload = false;\n				}\n				else\n				{\n					$data = array(''upload_data'' => $this->upload->data());\n					$file_name = $data[''upload_data''][''file_name''];\n	\n					//update database\n					$custom_file = array(\n										''user_id'' => $user_staff_id,\n										''attribute_name'' => $key,\n										''attributes'' => $file_name,\n										''file_upload'' => ''yes''\n									);										\n					$this->staff_model->insert_staff_custom_attributes($custom_file);\n				}\n			}\n		}\n		$this->session->set_flashdata(''load_document_tab'',true);\n		redirect(''staff/edit/''.$user_staff_id);\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(232, 77, 'delete_custom_document', 'public', 'Delete custom documents', '', 'int(staff custom attribute id)', '[{"attr_name":"return:","attr_desc":"\\t redirect to staff profile \\n\\t"}]', 'function delete_custom_document($staffs_custom_attributes_id)\n	{\n		$file_details = $this->staff_model->get_staff_custom_attribute_by_id($staffs_custom_attributes_id);\n		$file_name = $file_details->attributes;	\n		$staff_user_id = $file_details->user_id;\n		$folder =  md5(''custom_files''.$staff_user_id);\n		$this->_delete_document($folder,$file_name);\n		$this->staff_model->delete_staff_custom_attributes_by_id($staffs_custom_attributes_id);\n		$this->session->set_flashdata(''load_document_tab'',true);\n		redirect(''staff/edit/''.$staff_user_id);\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(233, 77, '_create_folders', 'private', 'Creates folder for documents', '', '(string) path of the folder, (string) salt, (array) array of subfolders if applicable', '[{"attr_name":"return:","attr_desc":"\\t returns the folder name to the control that called this function\\n\\t"}]', 'function _create_folders($path,$salt,$subfolders = null)\n	{	\n		//create staff specific folders\n		if($path && $salt){\n			$newfolder = md5($salt);\n			$dir = $path."/".$newfolder;\n			if(!is_dir($dir))\n			{\n			  mkdir($dir);\n			  chmod($dir,0777);\n			  $fp = fopen($dir.''/index.html'', ''w'');\n			  fwrite($fp, ''<html><head>Permission Denied</head><body><h3>Permission denied</h3></body></html>'');\n			  fclose($fp);\n			}\n			\n			$sub_dir = '''';\n			if($subfolders){\n				foreach($subfolders as $folder){\n					$sub_dir = $dir.''/''.$folder;	\n					if(!is_dir($sub_dir))\n					{\n					  mkdir($sub_dir);\n					  chmod($sub_dir,0777);\n					  $fp = fopen($sub_dir.''/index.html'', ''w'');\n					  fwrite($fp, ''<html><head>Permission Denied</head><body><h3>Permission denied</h3></body></html>'');\n					  fclose($fp);\n					}		\n				}\n			}\n			return $newfolder;\n		}\n		\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(234, 77, '_delete_document', 'private', 'Deletes custom documents', '', '(string) folder name, (string) file name', '[{"attr_name":"return:","attr_desc":"\\t null\\n\\t"}]', 'function _delete_document($folder,$document_name)\n	{\n		if($document_name && $folder){\n			$main_path = "./uploads/staff/custom_attributes/".$folder;\n			//delete doc\n			if(file_exists($main_path.''/''.$document_name)){\n				unlink($main_path.''/''.$document_name);\n			}\n		}\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(235, 77, '', 'public', 'Returns the gender of the staff', '', '(int) user id', '[{"attr_name":"name:get_staff_gender","attr_desc":"\\t\\n\\t"},{"attr_name":"returns","attr_desc":"\\t (string) m or f\\n\\t"}]', '', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(236, 77, 'delete_files', 'private', 'Deletes file from server', '', '(string) path to the main folder, (string) name of the file, (array) sub folder names', '[{"attr_name":"return:","attr_desc":"\\t null\\n\\t"}]', 'function delete_files($path,$file_name,$sub_folders = array())\n	{\n		if(file_exists($path.''/''.$file_name)){\n			unlink($path.''/''.$file_name);\n		}\n		if($sub_folders){\n			foreach($sub_folders as $folder){\n				if(file_exists($path.''/''.$folder.''/''.$file_name)){\n					unlink($path.''/''.$folder.''/''.$file_name);\n				}	\n			}\n		}\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(237, 77, 'custom_attributes_for_staff_profile', 'public', 'Shows the existing form for custom attributes in staff profile.', '', '(int) user id of staff', '[{"attr_name":"return","attr_desc":"\\t Loads existing form elements for custom attributes in staff profile.\\n\\t"}]', 'function custom_attributes_for_staff_profile($staff_user_id)\n	{\n		$data[''existing_elements''] = $this->formbuilder_model->get_form_elements(true);\n		$data[''user_id''] = $staff_user_id;\n		$this->load->view(''custom_attributes_for_staff_profile'',isset($data) ? $data : NULL);	\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(238, 77, 'custom_file_uploads_for_staff_profile', 'public', 'Shows the existing form for custom attributes in staff profile.', '', '(int) user id of staff', '[{"attr_name":"return","attr_desc":"\\t Loads existing file upload form elements for custom attributes in staff profile.\\n\\t"}]', 'function custom_file_uploads_for_staff_profile($staff_user_id)\n	{\n		$data[''existing_elements''] = $this->formbuilder_model->get_form_elements(false);\n		$data[''user_id''] = $staff_user_id;\n		$this->load->view(''custom_upload_fields_for_staff_profile'',isset($data) ? $data : NULL);	\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(239, 77, 'custom_attributes', 'public', 'Shows the existing form for custom attributes.', '', 'null', '[{"attr_name":"return","attr_desc":"\\t Loads existing form elements for custom attributes.\\n\\t"}]', 'function custom_attributes_for_staff_profile($staff_user_id)\n	{\n		$data[''existing_elements''] = $this->formbuilder_model->get_form_elements(true);\n		$data[''user_id''] = $staff_user_id;\n		$this->load->view(''custom_attributes_for_staff_profile'',isset($data) ? $data : NULL);	\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(240, 77, 'get_custom_attrs', 'public', 'This function gets all the posted value from the search staff page then filters and returns only the custom attributes search parameters to the calling method.', '', '(via POST) gets posted data from the search staff page', '[{"attr_name":"return","attr_desc":"\\t Returns the custom attributes only values from all the posted data \\n\\t"}]', 'function get_custom_attrs($search_params)\n	{\n		$custom_attrs = array();\n		$normal_prefix = ''custom_attrs_'';\n		$file_prefix = ''custom_file_'';\n		if($search_params){\n			foreach($search_params as $key => $val){\n				if($val){\n					if(substr($key,0,strlen($normal_prefix)) == $normal_prefix){\n						$new_key = substr($key, strlen($normal_prefix));\n						$custom_attrs[''normal_elements''][$new_key] = $val;	\n					}\n					if(substr($key,0,strlen($file_prefix)) == $file_prefix){\n						$new_key = substr($key, strlen($file_prefix));\n						$custom_attrs[''file_uploads''][$new_key] = $val;	\n					}\n				}\n			}	\n		}\n		return $custom_attrs;\n	\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(241, 77, 'send_email', 'public', 'Test function to send email from localhost', '', '', '', 'function send_email()\n	{\n		$config = Array(\n		  ''protocol'' => ''smtp'',\n		  ''smtp_host'' => ''ssl://smtp.googlemail.com'',\n		  ''smtp_port'' => 465,\n		  ''smtp_user'' => ''your@gmail.com'', // change it to yours\n		  ''smtp_pass'' => ''yourpassword'', // change it to yours\n		  ''mailtype'' => ''html'',\n		  ''charset'' => ''iso-8859-1'',\n		  ''wordwrap'' => TRUE\n		);\n		\n		$message = ''Test message'';\n		$this->load->library(''email'', $config);\n		$this->email->set_newline("\\r\\n");\n		$this->email->from(''kaushtuvgurung@gmail.com''); // change it to yours\n		$this->email->to(''rseptiane@gmail.com'');// change it to yours\n		$this->email->subject(''Testing Email from localhost'');\n		$this->email->message($message);\n		if($this->email->send()){\n		  echo ''Email sent.'';\n		}else{\n			show_error($this->email->print_debugger());\n		}\n	}\n	\n}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
