(243, 79, 'get_total_staffs_count', 'public', 'Returns total staff based on status. If status is not passed it returns the total staffs', '', 'string, int status', '[{"attr_name":"return:","attr_desc":"\\t (int) total staff count\\n\\t"}]', 'function get_total_staffs_count($status='''')\n	{\n		$sql = ''select count(staff_id) as total from user_staffs'';\n		if($status){\n			$sql .= '''';	\n		}\n		$total = $this->db->query($sql)->row();\n		if($total){\n			return $total->total;	\n		}\n		return 0;\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(244, 79, 'staff_has_role', 'public', 'Checks if a staff has been assigned this role.', '', 'null', '[{"attr_name":"return","attr_desc":"\\t Returns true if this role has been assigned to staff and vice versa\\n\\t"}]', 'function staff_has_role($staff_user_id,$role_id)\n	{\n		$query = $this->db->where(''user_id'',$staff_user_id)->where(''attribute_role_id'',$role_id)->get(''staffs_roles'')->row();\n		if($query){\n			return true;	\n		}\n		return false;\n		\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(245, 79, 'get_role_frequency', 'public', 'This function returns the frequency of the role. It returns the number of times this role has been assigned to staffs.', '', 'null', '[{"attr_name":"return","attr_desc":"\\t Return the number of times this roles has been assigned to staffs.\\n\\t"}]', 'function get_role_frequency($role_id)\n	{\n		$sql = "SELECT count(staffs_roles_id) as total FROM `staffs_roles` WHERE `attribute_role_id` = ".$role_id;\n		$total = $this->db->query($sql)->row_array();\n		if($total){\n			return $total[''total''];	\n		}\n		return 0;\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(246, 79, 'update_staff_role', 'public', 'Update staff role. Adds or deletes role based on user input', '', '(int) role_id, (int) user_staff_id', '[{"attr_name":"return:","attr_desc":"\\t null\\n\\t"}]', 'function update_staff_role($user_staff_id,$role_id)\n	{\n		$staff_has_role = $this->staff_has_role($user_staff_id,$role_id);\n		if($staff_has_role){\n			return $this->delete_staff_role($user_staff_id,$role_id);\n		}else{\n			return $this->add_staff_role($user_staff_id,$role_id);\n		}\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(247, 79, 'add_staff_role', 'public', 'Assign role to a staff', '', '(int) role_id, (int) user_staff_id', '[{"attr_name":"return:","attr_desc":"\\t null\\n\\t"}]', 'function add_staff_role($user_staff_id,$role_id)\n	{\n		$data = array(\n					''user_id'' => $user_staff_id,\n					''attribute_role_id'' => $role_id\n				);\n		$this->db->insert(''staffs_roles'', $data);\n		return $this->db->insert_id();\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
(248, 79, 'delete_staff_role', 'public', 'Delete role from the staff', '', '(int) role_id, (int) user_staff_id', '[{"attr_name":"return:","attr_desc":"\\t null\\n\\t"}]', 'function delete_staff_role($user_staff_id,$role_id)\n	{\n		$sql = "delete from staffs_roles where user_id = ".$user_staff_id." and attribute_role_id = ".$role_id;\n		return $this->db->query($sql);\n	}', '2014-02-28 05:18:32', '0000-00-00 00:00:00'),
